(()=>{const bg=document.querySelector('.parallax-hero video, .parallax-hero .bg');const onScroll=()=>{if(!bg)return;const y=window.scrollY||0;const t=Math.min(40,y*0.06);bg.style.transform=`translate3d(0,${t}px,0) scale(1.02)`};onScroll();addEventListener('scroll',onScroll,{passive:true})})();
(()=>{const toMag=[...document.querySelectorAll('.magnet')];toMag.forEach(el=>{el.addEventListener('mousemove',e=>{const r=el.getBoundingClientRect();const dx=(e.clientX-(r.left+r.width/2))/(r.width/2);const dy=(e.clientY-(r.top+r.height/2))/(r.height/2);el.style.setProperty('--mx',`${dx*6}px`);el.style.setProperty('--my',`${dy*6}px`)});el.addEventListener('mouseleave',()=>{el.style.removeProperty('--mx');el.style.removeProperty('--my')})})})();
